import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Dashboard() {
    const location = useLocation();
    const navigate = useNavigate();
    const userName = location.state?.userName || "Guest";

    const OnLogOutChange = () => {
        // 1. Remove JWT token from localStorage/sessionStorage (if stored there)
        localStorage.removeItem('token'); // or sessionStorage.removeItem('token');

        // 2. Remove JWT token from cookies
        // Set the cookie to expire in the past to remove it
        document.cookie = "token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";

        // 3. Redirect to login page (or any other page)
        navigate('/login');
    }

    return (
        <div className='form'>
            <h1>Welcome, {userName}!</h1>
            <button onClick={OnLogOutChange}>Logout</button>
        </div>
    );
}
